/** Define globals for JSLint **//*global $, jQuery,$window,console,iScroll*/function resizeDiv(e,t,n){e.css({width:t+"px",height:n+"px"})}$(document).ready(function(){var e=$(".cover");resizeDiv(e,$(window).width(),$(window).height());$(window).on("resize",function(){resizeDiv(e,$(window).width(),$(window).height())});$(window).scroll(function(){var t=$(window).scrollTop();if(t>=e.height()){$(".content").css("margin-top",e.height()+40+"px").removeClass("stick");$(".fullpage").css("height",e.height())}else t<=e.height&&$(".content").addClass("stick")})});